let table_options = {};
let loaded = false;
let table = null;

async function loadScripts(...urls) {
  for (const thisUrl of urls) {
    const response = await fetch(thisUrl);
    const responseBody = await response.text();

    const scriptElm = document.createElement("script");
    const inlineCode = document.createTextNode(responseBody);
    scriptElm.appendChild(inlineCode);
    document.body.appendChild(scriptElm);
  }
}

generateTable = (json_content) => {
  table = "<thead><tr>";
  const headers = Object.keys(json_content[0]);
  for (let index in headers) {
    table += `<th>${headers[index]}</th>`;
  }
  table += "</tr></thead>";
  table += "<tbody>";
  for (let index in json_content) {
    table += "<tr>";
    for (let header in headers) {
      table += "<td>";
      table += json_content[index][headers[header]];
      table += "</td>";
    }
    table += "</tr>";
  }
  table += "</tbody>";

  return table;
};

main = async (json_content) => {
  if (loaded == false) {
    await loadScripts(
      "https://cdn.datatables.net/2.0.2/js/dataTables.js",
      "https://cdn.datatables.net/select/2.0.0/js/dataTables.select.js",
      "https://cdn.datatables.net/select/2.0.0/js/select.dataTables.js",
      "https://cdn.datatables.net/buttons/3.0.1/js/dataTables.buttons.js",
      "https://cdn.datatables.net/buttons/3.0.1/js/buttons.dataTables.js"
    );

    loaded = true;
  }

  if (table != null) {
    table.destroy();
  }

  const dynamicTable = generateTable(json_content);
  const tableContainer = document.getElementById("myTable");
  tableContainer.innerHTML = dynamicTable;

  let merged = {
    ...{
      layout: {
        topStart: {
          buttons: [
            {
              text: "Select all",
              action: function () {
                table.rows().select();
              },
            },
            {
              text: "Select none",
              action: function () {
                table.rows().deselect();
              },
            },
          ],
        },
        bottomEnd: {
          pageLength: {
            menu: [10, 25, 50, 100],
          },
          paging: {
            numbers: 3,
          },
        },
      },
      select: true,
    },
    ...table_options,
  };

  table = new DataTable("#myTable", merged);

  table.on("select", function (e, dt, type, indexes) {
    computeSelected(dt.rows(".selected")[0]);
  });

  table.on("deselect", function (e, dt, type, indexes) {
    computeSelected(dt.rows(".selected")[0]);
  });
};

//document.addEventListener("DOMContentLoaded", async (event) => {
//});

computeSelected = (indexes) => {
  let selected = [];
  for (let i in indexes) {
    selected.push(json_content[indexes[i]]);
  }
  fireEvent("Selected", JSON.stringify(selected));
};

getValue("JSON", (tempVar) => {
  if (tempVar !== null) {
    json_content = JSON.parse(tempVar);
    main(json_content);
  }
});

getValue("tableOptions", (tempVar) => {
  if (tempVar !== null) {
    table_options = JSON.parse(tempVar);
  }
});

